import{_ as s,c as t,o as e,ae as l}from"./chunks/framework.B8KKhRA6.js";const d=JSON.parse('{"title":"自定义滚动条","description":"","frontmatter":{"layout":"doc","author":"刘军","location":"武汉","date":"2024-06-20T00:00:00.000Z"},"headers":[],"relativePath":"web/css/scrollbar.md","filePath":"web/css/scrollbar.md","lastUpdated":1718940490000}'),i={name:"web/css/scrollbar.md"};function r(o,a,n,c,h,p){return e(),t("div",null,a[0]||(a[0]=[l(`<h1 id="自定义滚动条" tabindex="-1">自定义滚动条 <a class="header-anchor" href="#自定义滚动条" aria-label="Permalink to &quot;自定义滚动条&quot;">​</a></h1><h3 id="标准的css属性" tabindex="-1">标准的css属性 <a class="header-anchor" href="#标准的css属性" aria-label="Permalink to &quot;标准的css属性&quot;">​</a></h3><h4 id="_1、scrollbar-color" tabindex="-1">1、scrollbar-color <a class="header-anchor" href="#_1、scrollbar-color" aria-label="Permalink to &quot;1、scrollbar-color&quot;">​</a></h4><p>设置滚动条轨道（track）和滑块（thumb）的颜色</p><div class="language-css vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">scrollbar-color</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: auto | 滑杆颜色 轨道颜色;</span></span></code></pre></div><h4 id="_2、scrollbar-width" tabindex="-1">2、scrollbar-width <a class="header-anchor" href="#_2、scrollbar-width" aria-label="Permalink to &quot;2、scrollbar-width&quot;">​</a></h4><p>在元素显示滚动条时设置滚动条的最大宽度</p><div class="language-css vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">scrollbar-width</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: auto | thin | none;</span></span></code></pre></div><p>auto 就是默认的尺寸，在 Windows 系统下是 17px；<br> thin 是窄滚动条，在 Windows 系统下是 8px；<br> none 没有滚动条，宽度为0，但是内容依然可以滚动</p><h4 id="_3、scrollbar-gutter" tabindex="-1">3、scrollbar-gutter <a class="header-anchor" href="#_3、scrollbar-gutter" aria-label="Permalink to &quot;3、scrollbar-gutter&quot;">​</a></h4><p>可以让滚动条出现的时候内容不晃动</p><div class="language-css vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">scrollbar-gutter</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: auto;</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">scrollbar-gutter</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: stable;</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">scrollbar-gutter</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: stable </span><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">both-edges</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">;</span></span></code></pre></div><p>auto 浏览器默认表现<br> stable 浏览器提前预留好滚动条的空白区域<br> both-edges 浏览器在左右两侧同时预留一样的空白位置 目的是让内容局部是绝对居中的</p><h3 id="非标准-但是一直被广泛使用的属性" tabindex="-1">非标准，但是一直被广泛使用的属性 <a class="header-anchor" href="#非标准-但是一直被广泛使用的属性" aria-label="Permalink to &quot;非标准，但是一直被广泛使用的属性&quot;">​</a></h3><p>::-webkit-scrollbar — 整个滚动条，可以设置为0让滚动条不可见<br> ::-webkit-scrollbar-button — 滚动条上的按钮 (上下箭头)<br> ::-webkit-scrollbar-thumb — 滚动条上的滚动滑块<br> ::-webkit-scrollbar-track — 滚动条轨道<br> ::-webkit-scrollbar-track-piece — 滚动条没有滑块的轨道部分，不常用，可忽略<br> ::-webkit-scrollbar-corner — 当同时有垂直滚动条和水平滚动条时交汇的部分，用得少，可忽略<br> ::-webkit-resizer — 出现在某些元素底角的可拖动调整大小的滑块，例如textarea的右下角那个可拖动区域</p>`,15)]))}const k=s(i,[["render",r]]);export{d as __pageData,k as default};
